processor.condition:
CREATE PROCEDURE condition @_ExecutePlugin BOOLEAN OUTPUT @ARExternalNameSpace VARCHAR @AROperation ENUM -- $ICE:('metratech.com','Operation')$ @ContactType ENUM -- $ICE:('metratech.com/accountcreation','ContactType')$ AS -- We should only modify the t_account_mapper table for Add and Delete but not for Update.
SET @_ExecutePlugin = ((@AROperation != #metratech.com/Operation/Update#) AND (@ARExternalNameSpace != '') AND (@ContactType = #metratech.com/accountcreation/ContactType/Bill-To#))
-- IF @AROperation IS NULL -- PRINT 'ARModifyAccountMapping: ARExternalNameSpace [' + @ARExternalNameSpace + '] AROperation [NULL] ContactType [' + CAST(@ContactType AS VARCHAR) + '] _ExecutePlugin [' + CAST(@_ExecutePlugin AS VARCHAR) + ']'; -- ELSE -- PRINT 'ARModifyAccountMapping: ARExternalNameSpace [' + @ARExternalNameSpace + '] AROperation [' + CAST(@AROperation AS VARCHAR) + '] ContactType [' + CAST(@ContactType AS VARCHAR) + '] _ExecutePlugin [' + CAST(@_ExecutePlugin AS VARCHAR) + ']';
