configdata.Program:
    CREATE PROCEDURE SetTax
  @_FedTax DECIMAL OUTPUT
  @_StateTax DECIMAL OUTPUT
  @_CountyTax DECIMAL OUTPUT
  @_LocalTax DECIMAL OUTPUT
  @_OtherTax DECIMAL OUTPUT
AS
  SET @_FedTax    = 0.0
  SET @_StateTax  = 0.0
  SET @_CountyTax = 0.0
  SET @_LocalTax  = 0.0
  SET @_OtherTax  = 0.0
