configdata.Program:
-- We are choosing the point of time, when we look up the rates. -- For subscription charges we will use the subscription start date for rate lookup -- For unsubscription charges we will use the subscription end date for rate lookup CREATE PROCEDURE RateDate @NRCEventType INTEGER @NRCIntervalSubscriptionStart DATETIME @NRCIntervalSubscriptionEnd DATETIME @_timestamp DATETIME OUTPUT AS --PRINT 'ratedate: NRCIntervalSubscriptionStart = ' + CAST( @NRCIntervalSubscriptionStart AS VARCHAR) + '; NRCIntervalSubscriptionEnd = ' + CAST( @NRCIntervalSubscriptionEnd AS VARCHAR);
IF @NRCEventType = 1 -- Subscription Event BEGIN SET @_timestamp = @NRCIntervalSubscriptionStart; END ELSE BEGIN IF @NRCEventType = 2 -- Unsubscription Event BEGIN SET @_timestamp = @NRCIntervalSubscriptionEnd; END ELSE BEGIN -- Unknown event type PRINT 'ratedate: Unknown NRCEventType of [' + CAST(@NRCEventType AS VARCHAR) + '] was metered. Using Subscription Start Date for rate lookup by default.' SET @_timestamp = @NRCIntervalSubscriptionStart; END END
PRINT 'ratedate: nrcratelookup uses rates effective on ' + CAST( @_timestamp AS VARCHAR);
